import React, { useState } from 'react';
import axios from 'axios';

const ProductManagement = () => {
  // State for the login form
  const [credentials, setCredentials] = useState({
    username: '',
    password: '',
  });
  const [loginError, setLoginError] = useState('');
  const [isLoginLoading, setIsLoginLoading] = useState(false);

  // State for the product form
  const [product, setProduct] = useState({
    name: '',
    description: '',
    category: '',
    price: '',
    quantity: '',
  });
  const [productMessage, setProductMessage] = useState('');

  // Handlers for the login form
  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setLoginError('');
    setIsLoginLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/login', credentials);
      alert(res.data.message);
      setCredentials({ username: '', password: '' });
    } catch (err) {
      console.error(err);
      setLoginError(err.response?.data?.message || 'Error logging in.');
    } finally {
      setIsLoginLoading(false);
    }
  };

  // Handlers for the product form
  const handleProductChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleProductSubmit = async (e) => {
    e.preventDefault();
    setProductMessage('');

    try {
      const res = await axios.post('http://localhost:5000/products', product);
      setProductMessage(res.data.message || 'Product added successfully!');
      setProduct({
        name: '',
        description: '',
        category: '',
        price: '',
        quantity: '',
      });
    } catch (err) {
      console.error(err);
      setProductMessage(err.response?.data?.message || 'Error adding product.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Product Management</h1>
      <div style={{ display: 'flex', gap: '40px', marginTop: '20px' }}>
        {/* User Login Form */}
        <div style={{ flex: 1, border: '1px solid #ccc', padding: '20px', borderRadius: '8px' }}>
          <h2>User Login</h2>
          <form onSubmit={handleLoginSubmit}>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleLoginChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleLoginChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
            <button
              type="submit"
              disabled={isLoginLoading}
              style={{
                padding: '10px 20px',
                backgroundColor: isLoginLoading ? '#ccc' : '#007bff',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: isLoginLoading ? 'not-allowed' : 'pointer',
              }}
            >
              {isLoginLoading ? 'Logging in...' : 'Login'}
            </button>
          </form>
        </div>

        {/* Product Form */}
        <div style={{ flex: 1, border: '1px solid #ccc', padding: '20px', borderRadius: '8px' }}>
          <h2>Add Product</h2>
          <form onSubmit={handleProductSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Product Name"
              value={product.name}
              onChange={handleProductChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={product.description}
              onChange={handleProductChange}
              required
              style={{
                padding: '10px',
                width: '100%',
                height: '80px',
                marginBottom: '10px',
                borderRadius: '4px',
              }}
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={product.category}
              onChange={handleProductChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={product.price}
              onChange={handleProductChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            <input
              type="number"
              name="quantity"
              placeholder="Quantity"
              value={product.quantity}
              onChange={handleProductChange}
              required
              style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '4px' }}
            />
            {productMessage && <p style={{ color: 'green' }}>{productMessage}</p>}
            <button
              type="submit"
              style={{
                padding: '10px 20px',
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Add Product
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProductManagement;
