// ProductManagement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({ name: '', description: '', category: '', price: 0, quantity: 0 });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:5000/products')
      .then((res) => setProducts(res.data))
      .catch((err) => console.error(err));
  };

  const addProduct = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/products', product)
      .then((res) => {
        setProducts([...products, res.data]);
        setProduct({ name: '', description: '', category: '', price: 0, quantity: 0 });
      })
      .catch((err) => console.error(err));
  };

  const deleteProduct = (id) => {
    axios.delete(`http://localhost:5000/products/${id}`)
      .then(() => setProducts(products.filter((p) => p.id !== id)))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h1>Product Management</h1>
      <form onSubmit={addProduct}>
        <input placeholder="Name" value={product.name} onChange={(e) => setProduct({ ...product, name: e.target.value })} />
        <input placeholder="Description" value={product.description} onChange={(e) => setProduct({ ...product, description: e.target.value })} />
        <input placeholder="Category" value={product.category} onChange={(e) => setProduct({ ...product, category: e.target.value })} />
        <input type="number" placeholder="Price" value={product.price} onChange={(e) => setProduct({ ...product, price: parseFloat(e.target.value) })} />
        <input type="number" placeholder="Quantity" value={product.quantity} onChange={(e) => setProduct({ ...product, quantity: parseInt(e.target.value) })} />
        <button type="submit">Add Product</button>
      </form>
      <ul>
        {products.map((prod) => (
          <li key={prod.id}>
            {prod.name} - {prod.quantity}
            <button onClick={() => deleteProduct(prod.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductManagement;
