import React, { useEffect, useState } from 'react';
import { Bar, Pie, Line } from 'react-chartjs-2';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = ({ onLogout }) => {
  const [products, setProducts] = useState([]);
  const [metrics, setMetrics] = useState({
    totalProducts: 0,
    totalQuantity: 0,
    totalValue: 0,
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/products');
      const data = response.data;
      setProducts(data);

      // Calculate metrics
      const totalProducts = data.length;
      const totalQuantity = data.reduce((sum, prod) => sum + prod.quantity, 0);
      const totalValue = data.reduce((sum, prod) => sum + prod.price * prod.quantity, 0);

      setMetrics({ totalProducts, totalQuantity, totalValue });
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleLogout = () => {
    onLogout();
    navigate('/login');
  };

  // Chart Data Preparation
  const categories = [...new Set(products.map((p) => p.category))];
  const productsPerCategory = categories.map(
    (cat) => products.filter((p) => p.category === cat).length
  );
  const inventoryByCategory = categories.map(
    (cat) => products.filter((p) => p.category === cat).reduce((sum, p) => sum + p.quantity, 0)
  );

  const lineChartData = {
    labels: products.map((p) => p.name),
    datasets: [
      {
        label: 'Price',
        data: products.map((p) => p.price),
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <nav>
        <button onClick={handleLogout} className="logout-button">
          Logout
        </button>
      </nav>
      <h1>Business Intelligence Dashboard</h1>

      <div className="metrics">
        <div className="metric-card">
          <h3>Total Products</h3>
          <p>{metrics.totalProducts}</p>
        </div>
        <div className="metric-card">
          <h3>Total Quantity</h3>
          <p>{metrics.totalQuantity}</p>
        </div>
        <div className="metric-card">
          <h3>Total Value</h3>
          <p>${metrics.totalValue.toFixed(2)}</p>
        </div>
      </div>

      <div className="charts">
        <div className="chart-container">
          <h3>Products per Category</h3>
          <Bar
            data={{
              labels: categories,
              datasets: [
                {
                  label: 'Products',
                  data: productsPerCategory,
                  backgroundColor: 'rgba(54, 162, 235, 0.6)',
                },
              ],
            }}
          />
        </div>
        <div className="chart-container">
          <h3>Inventory Distribution by Category</h3>
          <Pie
            data={{
              labels: categories,
              datasets: [
                {
                  label: 'Inventory',
                  data: inventoryByCategory,
                  backgroundColor: [
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(75, 192, 192, 0.6)',
                  ],
                },
              ],
            }}
          />
        </div>
        <div className="chart-container">
          <h3>Price Trends</h3>
          <Line data={lineChartData} />
        </div>
      </div>

      <div className="product-table">
        <h3>Product Details</h3>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Category</th>
              <th>Price</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>{product.category}</td>
                <td>${product.price.toFixed(2)}</td>
                <td>{product.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
