import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/products');
      setProducts(res.data);

      // Extract unique categories
      const uniqueCategories = [...new Set(res.data.map((prod) => prod.category))];
      setCategories(uniqueCategories);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  // Prepare data for bar chart
  const chartData = {
    labels: products.map((prod) => prod.name), // Product names
    datasets: [
      {
        label: 'Quantity',
        data: products.map((prod) => prod.quantity), // Product quantities
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Product Dashboard</h1>

      <div style={{ marginBottom: '20px' }}>
        <h2>Key Metrics</h2>
        <p><strong>Total Products:</strong> {products.length}</p>
        <p><strong>Total Categories:</strong> {categories.length}</p>
      </div>

      <div style={{ width: '80%', margin: '0 auto' }}>
        <h2>Product Quantities</h2>
        <Bar
          data={chartData}
          options={{
            responsive: true,
            plugins: {
              legend: { display: true, position: 'top' },
            },
            scales: {
              y: {
                beginAtZero: true,
              },
            },
          }}
        />
      </div>
    </div>
  );
};

export default Dashboard;
